  const [counter, setCounter] = useState(null);

  useEffect(() => {
    let i = 0;
    setTimeout(() => {
      setCounter((c) => {
        console.log(c, i);
        return i;
      });
      i = i + 1;
      setTimeout(() => {
        setCounter((c) => {
          console.log(c, i);
          return i;
        });
        i = i + 1;
        setTimeout(() => {
          setCounter((c) => {
            console.log(c, i);
            return i;
          });
          i = i + 1;
        }, 500);
      }, 500);
    }, 500);
  }, []);
  return <>{counter && counter}</>;
}
